<script>
    $('.alamatCheck').change((e) => {
        var y = document.getElementById("alamat").value
        var x = document.getElementById("alamatDomisili");
        if ($(e.target).is(":checked")) {
            x.value = y;
        } else {
            x.value = '';
        }
    })
</script>

<script>
    function alertNext() {
        var inputnama = document.getElementById('namaDepan').value;
        // alert('Data' + " " + inputnama + " " + "telah ditambahkan");
        Swal.fire({
            title: 'Tambah Data?',
            text: "Apakah anda yakin untuk menambah data " + inputnama + "?",
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yakin!',
            icon: 'warning',
        }).then((result) => {
            if (result.isConfirmed) {
                Swal.fire(
                        'Selesai!',
                        'Data ' + inputnama + " sudah ditambahkan",
                        'success'
                    )
                    .then(function() {
                        window.location = "/tambah-datakepegawaian";
                    });
            }
        });
    }
</script>

<script>
    const togglePassword = document.querySelector("#togglePassword");
    const password = document.querySelector("#password");

    togglePassword.addEventListener("click", function() {
        // toggle the type attribute
        const type = password.getAttribute("type") === "password" ? "text" : "password";
        password.setAttribute("type", type);

        // toggle the icon
        this.classList.toggle("bi-eye");
    });

    // prevent form submit
    const form = document.querySelector("form");
    form.addEventListener('submit', function(e) {
        e.preventDefault();
    });

    function preview() {
        frame.src = URL.createObjectURL(event.target.files[0]);
    }
</script>


